import React from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { FaVideo, FaDownload } from "react-icons/fa";

const extensions = [
  {
    name: "Extension 1",
    zipLink: "/files/extension1.zip",
    demoLink: "https://example.com/demo1",
    instructions: "Extract the zip file and load it in Chrome using 'Load unpacked' from the Extensions menu."
  },
  {
    name: "Extension 2",
    zipLink: "/files/extension2.zip",
    demoLink: "https://example.com/demo2",
    instructions: "After extracting, go to Chrome Extensions, enable Developer mode, and load the folder as an unpacked extension."
  },
  {
    name: "Extension 3",
    zipLink: "/files/extension3.zip",
    demoLink: "https://example.com/demo3",
    instructions: "Download the zip file, unzip it, and load the folder in Chrome Extensions using 'Load unpacked'."
  }
];

const ExtensionCard = ({ name, zipLink, demoLink, instructions }) => (
  <Card className="p-4 rounded-2xl shadow-md mb-4">
    <CardContent>
      <h2 className="text-xl font-bold mb-2">{name}</h2>
      <p className="text-sm mb-2">{instructions}</p>
      <div className="flex gap-2">
        <Button asChild className="bg-blue-500 text-white hover:bg-blue-600">
          <a href={zipLink} download><FaDownload className="mr-2" /> Download</a>
        </Button>
        <Button asChild className="bg-green-500 text-white hover:bg-green-600">
          <a href={demoLink} target="_blank" rel="noopener noreferrer"><FaVideo className="mr-2" /> Demo Video</a>
        </Button>
      </div>
    </CardContent>
  </Card>
);

const ExtensionsPage = () => {
  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Chrome Extensions Hub</h1>
      {extensions.map((ext, index) => (
        <ExtensionCard key={index} {...ext} />
      ))}
    </div>
  );
};

export default ExtensionsPage;
