import React from 'react';
// Correct the import paths
import Card from './ui/card';
import Button from './ui/button';
import { FaDownload } from "react-icons/fa";
import './ExtensionsPage.css'; // Import the CSS file

// Define CardContent if it doesn't exist
const CardContent = ({ children }) => (
  <div className="card-content">
    {children}
  </div>
);

const extensions = [
  {
    name: "Extension 1",
    zipLink: "/files/extension1.zip",
    demoLink: "https://example.com/demo1.mp4",
    instructions: "Extract the zip file and load it in Chrome using 'Load unpacked' from the Extensions menu."
  },
  {
    name: "Extension 2",
    zipLink: "/files/extension2.zip",
    demoLink: "https://example.com/demo2.mp4",
    instructions: "After extracting, go to Chrome Extensions, enable Developer mode, and load the folder as an unpacked extension."
  },
  {
    name: "Extension 3",
    zipLink: "/files/extension3.zip",
    demoLink: "https://example.com/demo3.mp4",
    instructions: "Download the zip file, unzip it, and load the folder in Chrome Extensions using 'Load unpacked'."
  }
];

const ExtensionCard = ({ name, zipLink, demoLink, instructions }) => (
  <Card className="p-4 rounded-2xl shadow-md mb-4 flex flex-col md:flex-row">
    <CardContent className="flex-1">
      <h2 className="text-xl font-bold mb-2">{name}</h2>
      <p className="text-sm mb-2">{instructions}</p>
      <div className="flex gap-2 mb-2">
        <Button asChild className="bg-blue-500 text-white hover:bg-blue-600">
          <a href={zipLink} download><FaDownload className="mr-2" /> Download</a>
        </Button>
      </div>
    </CardContent>
    <video controls className="w-full md:w-1/2 rounded-lg">
      <source src={demoLink} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  </Card>
);

const ExtensionsPage = () => {
  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">AccesiGen</h1>
      <div className="extensions-grid">
        {extensions.map((ext, index) => (
          <ExtensionCard key={index} {...ext} />
        ))}
      </div>
    </div>
  );
};

export default ExtensionsPage;
