import React, { useState } from 'react';
import { FaMoon, FaSun, FaDownload } from 'react-icons/fa';
import themes from './themeConfig';
import './ExtensionsPage.css';

const ExtensionsPage = () => {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    Object.entries(themes[newTheme]).forEach(([key, value]) => {
      document.documentElement.style.setProperty(key, value);
    });
  };

  const extensions = [
    {
      name: 'Extension 1',
      zipLink: '/files/extension1.zip',
      demoLink: 'https://example.com/demo1.mp4'
    },
    {
      name: 'Extension 2',
      zipLink: '/files/extension2.zip',
      demoLink: 'https://example.com/demo2.mp4'
    },
    {
      name: 'Extension 3',
      zipLink: '/files/extension3.zip',
      demoLink: 'https://example.com/demo3.mp4'
    }
  ];

  const instructions = `To install extensions:
1. Extract the downloaded zip file.
2. Go to Chrome > Extensions.
3. Enable Developer Mode.
4. Click 'Load Unpacked' and select the extracted folder.`;

  return (
    <div className="container">
      <div className="header">
        <h1>AccesiGen</h1>
        <button className="btn-theme-toggle" onClick={toggleTheme}>
          {theme === 'light' ? <FaMoon /> : <FaSun />} Toggle Theme
        </button>
      </div>
      <div className="instructions-card">
        <h2>Installation Instructions</h2>
        <p>{instructions}</p>
      </div>
      <div className="extensions-grid">
        {extensions.map((ext, index) => (
          <div key={index} className="glass-card">
            <h2>{ext.name}</h2>
            <div className="button-group">
              <a href={ext.zipLink} download className="btn-primary">
                <FaDownload /> Download
              </a>
            </div>
            <video controls className="video-preview">
              <source src={ext.demoLink} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ExtensionsPage;
