import React from 'react';
import { FaDownload, FaChrome, FaStar } from "react-icons/fa";
import './ExtensionsPage.css';

const ExtensionCard = ({ name, zipLink, demoLink, description, features }) => (
  <div className="glass-card">
    <h2 className="card-title">
      <FaChrome className="title-icon" />
      {name}
    </h2>
    <p className="description">{description}</p>
    <ul className="features-list">
      {features?.map((feature, index) => (
        <li key={index}>
          <FaStar className="feature-icon" /> {feature}
        </li>
      ))}
    </ul>
    <div className="button-group">
      <a href={zipLink} download className="btn-primary">
        <FaDownload /> Download Extension
      </a>
    </div>
    <video controls className="video-preview" poster={`/thumbnails/${name}.jpg`}>
      <source src={demoLink} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  </div>
);

export default ExtensionCard;
