import React from 'react';
import { FaDownload, FaTools } from "react-icons/fa";
import './ExtensionsPage.css';

const extensions = [
  {
    name: "Extension 1",
    zipLink: "/files/extension1.zip",
    demoLink: "https://example.com/demo1.mp4",
    instructions: "Extract the zip file and load it in Chrome using 'Load unpacked' from the Extensions menu."
  },
  {
    name: "Extension 2",
    zipLink: "/files/extension2.zip",
    demoLink: "https://example.com/demo2.mp4",
    instructions: "After extracting, go to Chrome Extensions, enable Developer mode, and load the folder as an unpacked extension."
  },
  {
    name: "Extension 3",
    zipLink: "/files/extension3.zip",
    demoLink: "https://example.com/demo3.mp4",
    instructions: "Download the zip file, unzip it, and load the folder in Chrome Extensions using 'Load unpacked'."
  }
];

const ExtensionCard = ({ name, zipLink, demoLink, instructions }) => (
  <div className="glass-card">
    <h2 className="card-title">{name}</h2>
    <div className="card-content">
      <FaTools className="text-accent mb-2" />
      <p>{instructions}</p>
    </div>
    <div className="button-group">
      <a href={zipLink} download className="btn-primary">
        <FaDownload /> Download Extension
      </a>
    </div>
    <video controls className="video-preview">
      <source src={demoLink} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  </div>
);

const ExtensionsPage = () => {
  return (
    <div className="container">
      <header className="page-header">
        <h1 className="page-title">AccesiGen</h1>
        <p className="subtitle">Chrome Extension Management Made Easy</p>
      </header>
      <div className="extensions-grid">
        {extensions.map((ext, index) => (
          <ExtensionCard key={index} {...ext} />
        ))}
      </div>
    </div>
  );
};

export default ExtensionsPage;
