import React from 'react';
import { FaDownload, FaTools, FaChrome, FaLightbulb } from "react-icons/fa";
import './ExtensionsPage.css';

const extensions = [
  {
    name: "Smart Navigation",
    description: "Enhanced browsing with intelligent navigation features",
    zipLink: "/files/extension1.zip",
    demoLink: "https://example.com/demo1.mp4",
    features: ["Keyboard shortcuts", "Quick search", "Tab management"]
  },
  {
    name: "Productivity Suite",
    description: "Boost your workflow with powerful productivity tools",
    zipLink: "/files/extension2.zip",
    demoLink: "https://example.com/demo2.mp4",
    features: ["Task management", "Time tracking", "Focus mode"]
  },
  {
    name: "Web Enhancer",
    description: "Transform your browsing experience with advanced features",
    zipLink: "/files/extension3.zip",
    demoLink: "https://example.com/demo3.mp4",
    features: ["Dark mode", "Reader view", "Custom styles"]
  }
];

const ExtensionCard = ({ name, zipLink, demoLink, description, features }) => (
  <div className="glass-card">
    <h2 className="card-title">
      <FaChrome className="text-accent" />
      {name}
    </h2>
    <div className="card-content">
      <p className="description">{description}</p>
      <ul className="features-list">
        {features.map((feature, index) => (
          <li key={index}>
            <FaLightbulb className="feature-icon" /> {feature}
          </li>
        ))}
      </ul>
    </div>
    <div className="button-group">
      <a href={zipLink} download className="btn-primary">
        <FaDownload /> Download Extension
      </a>
    </div>
    <video controls className="video-preview">
      <source src={demoLink} type="video/mp4" />
      Your browser does not support the video tag.
    </video>
  </div>
);

const ExtensionsPage = () => {
  return (
    <div className="container">
      <header className="page-header">
        <h1 className="page-title">AccesiGen</h1>
        <p className="subtitle">
          Enhance your browsing experience with powerful Chrome extensions
        </p>
      </header>
      <div className="extensions-grid">
        {extensions.map((ext, index) => (
          <ExtensionCard key={index} {...ext} />
        ))}
      </div>
    </div>
  );
};

export default ExtensionsPage;
